import logo from "../../images/logo.svg";
import { Link, Route, useLocation } from "react-router-dom";
import './Header.css'
import NavTab from "../NavTab/NavTab";

function Header() {
  const location = useLocation();
  return (
    <header className={`header header__container header__container_theme_${
      location.pathname === '/' ? 'bright' : 'dark'
    }`}>
      <Route exact path="/">
      <Link to='/'>
          <img src={logo} className="header__logo" alt='логотип'/>
        </Link>
        <NavTab/>
      </Route>
      <Route  path="/movies">
      <Link to='/'>
          <img src={logo} className="header__logo" alt='логотип'/>
        </Link>
        <NavTab/>
      </Route>
      <Route  path="/signup">
      <Link to='/'>
          <img src={logo} className="header__logo" alt='логотип'/>
        </Link>
      </Route>
    </header>


    // <header className="header page__header">
    //   <img className="header__logo" src={logo} alt="логотип Mesto" />
    //   <div className="header__container">
    //     <Route path="/sign-up">
    //       <Link className="auth__link" to="/sign-in">
    //         Войти
    //       </Link>
    //     </Route>
    //     <Route path="/sign-in">
    //       <Link to="/sign-up" className="auth__link">
    //         Регистрация
    //       </Link>
    //     </Route>
    //     <Route exact path="/">
    //       <p className="auth__email">{email}</p>
    //       <Link to="/sign-in" className="auth__link" onClick={onLogOut}>
    //         Выйти
    //       </Link>
    //     </Route>
    //   </div>
    // </header>
  );
}

export default Header;
