import React from "react";

function Input(name, value, type, label) {
  return (
    <div>
      <label for={name} className="sign__label">
        {label}
      </label>
      <input
        name={name}
        type={type}
        className="sign__input"
        value={value}
        minLength="2"
        maxLength="30"
        autoComplete="off"
        //   value={values.name || ''}
        //   onChange={handleChange}
        required
      />
      <span id="sign-error" className="error"></span>
    </div>
  );
}

export default Input;
